import com.atlassian.jira.ComponentManager
import com.atlassian.jira.issue.fields.CustomField
import com.atlassian.jira.issue.CustomFieldManager
import com.atlassian.jira.issue.IssueManager
import com.atlassian.jira.component.ComponentAccessor
import org.apache.log4j.Level

import groovy.sql.Sql
import java.sql.Connection
import org.ofbiz.core.entity.ConnectionFactory
import org.ofbiz.core.entity.DelegatorInterface
import com.atlassian.jira.issue.util.IssueChangeHolder
import com.atlassian.jira.issue.util.DefaultIssueChangeHolder
import com.atlassian.jira.issue.ModifiedValue

import org.apache.log4j.Category
Category log = Category.getInstance("com.onresolve.jira.groovy.PostFunction")
log.setLevel(org.apache.log4j.Level.DEBUG)

CustomFieldManager customFieldManager = ComponentAccessor.getCustomFieldManager()
IssueManager issueManager = ComponentAccessor.getIssueManager()

CustomField request_description = customFieldManager.getCustomFieldObject(Long.parseLong("15800"))
CustomField status_description = customFieldManager.getCustomFieldObject(Long.parseLong("15801"))
CustomField send_to_customer = customFieldManager.getCustomFieldObject(Long.parseLong("15814"))
Object companyValue = issue.getCustomFieldValue(request_description)
def indexManager = ComponentAccessor.getIssueIndexManager()

IssueChangeHolder changeHolder = new DefaultIssueChangeHolder()

ComponentManager componentManager = ComponentManager.getInstance()
DelegatorInterface delegator = (DelegatorInterface) componentManager.getComponentInstanceOfType(DelegatorInterface.class)
String helperName = delegator.getGroupHelperName("default");

//Select request description on service desk
def sqlStmt = "SELECT STATUS_NAME FROM AO_54307E_STATUSMAPPING WHERE STATUS_ID = 11927 AND FORM_ID = (SELECT ID FROM AO_54307E_VIEWPORTFORM WHERE ISSUE_TYPE_ID = " + issue.getIssueTypeObject().id + " AND VIEWPORT_ID = (SELECT ID FROM AO_54307E_VIEWPORT WHERE PROJECT_ID = " + issue.getProjectObject().id + "))"
Connection conn = ConnectionFactory.getConnection(helperName);
Sql sql = new Sql(conn)
String status_desc //= new StringBuffer()
sql.eachRow(sqlStmt) {
	status_desc = it.STATUS_NAME
}

issue.setCustomFieldValue(status_description, status_desc)
indexManager.reIndex(issue)
issue.store()

//CustomField cfParent = customFieldManager.getCustomFieldObjectByName('Comment visibility')
//log.debug "transient vars:" + transientVars.get("changeItems")
//log.debug "custom field: " + transientVars.get("issue")

/*List changeItems = transientVars["changeItems"] as List
changeItems.each{Object cib ->
    log.debug cib["field"]// == "Comment visibility"
}//?.get("toString")*/
/*
log.debug changeItems.find{Object cib ->
    cib["field"] == "Comment visibility"
}["to"]
*/
//log.debug transientVars["changeItems"].find{Object cib -> cib["field"] == "Comment visibility"}
//log.debug transientVars["changeItems"].find{Object cib -> cib["field"] == "Comment visibility"}

if(transientVars["changeItems"].find{Object cib -> cib["field"] == "Comment visibility"} != null){
    //log.debug "aggiorno custom field value"
    issue.setCustomFieldValue(send_to_customer, transientVars["changeItems"].find{Object cib -> cib["field"] == "Comment visibility"}["to"].toString().replace("[", "").replace("]", ""))
}

