import com.atlassian.jira.ComponentManager
import org.apache.log4j.Category
 
def Category log = Category.getInstance("com.onresolve.jira.groovy.PostFunction")
log.setLevel(org.apache.log4j.Level.DEBUG)
  
def componentManager = ComponentManager.getInstance()
def changeHistoryManager = componentManager.getChangeHistoryManager()

changeHistoryManager.getChangeItemsForField(issue, "status").getAt(changeHistoryManager.getChangeItemsForField(issue, "status").size()) != 0 && changeHistoryManager.getChangeItemsForField(issue, "status").getAt(changeHistoryManager.getChangeItemsForField(issue, "status").size() - 2).getFromString().equals("1 - Queue to start") || changeHistoryManager.getChangeItemsForField(issue, "status").getAt(changeHistoryManager.getChangeItemsForField(issue, "status").size() - 2).getFromString().equals("2 - Engineering WIP") && issue.getCustomFieldValue(componentManager.getCustomFieldManager().getCustomFieldObjectByName("EC Type")).value != "Standard EC"

import com.atlassian.jira.component.ComponentAccessor import com.atlassian.crowd.embedded.api.User import com.atlassian.jira.issue.CustomFieldManager import com.atlassian.jira.user.ApplicationUser import com.atlassian.jira.issue.fields.CustomField
def watcherManager = ComponentAccessor.getWatcherManager() def userManager = ComponentAccessor.getUserManager()
CustomField ecType = ComponentAccessor.getCustomFieldManager().getCustomFieldObject("12800".toLong())
ApplicationUser user = ComponentAccessor.getUserUtil().getUserByKey("claudio.miccoli") ApplicationUser user2 = ComponentAccessor.getUserUtil().getUserByKey("luigi.pasquali") ApplicationUser user3 = ComponentAccessor.getUserUtil().getUserByKey("thomas.kolbe") if (user && issue.getCustomFieldValue(ecType).toString().equals("Mechanical CR")){ watcherManager.startWatching(user, issue) watcherManager.startWatching(user2, issue) watcherManager.startWatching(user3, issue) }

