 def currentUser = ComponentAccessor.getJiraAuthenticationContext().getLoggedInUser()
 def mi = (MutableIssue) issue

 fieldConfig = pmstatus.getRelevantConfig(issue)
 //log.warn "fieldConfig: " + fieldConfig
 cfNewValue = ComponentAccessor.optionsManager.getOptions(fieldConfig)?.find { it.toString() == status }

 pmstatus.updateValue(null, issue, new ModifiedValue(issue.getCustomFieldValue(pmstatus), cfNewValue), new DefaultIssueChangeHolder())
 issueIndexingService.reIndex(issue)

